import React, { useState } from "react";

const Toggle = () => {
  // 1.enabling state: useState[]
  // 2.initiazle state:
  // 3.reading state
  // 4.update state
  const array = useState(false);
  console.log(array);
  const [on, setOn] = useState(false);
  return (
    <div className="toggle" onClick={() => setOn(true)}>
      Toggle {on ? "On" : "Off"}
    </div>
  );
};

export default Toggle;
