import "./ToggleStyle.css";
const Toggle = () => {
  // 1.enabling state: useState[]
  // 2.initiazle state:
  // 3.reading state
  // 4.update state

  const array = useState(false);
  console.log(array);
  const [on, setOn] = useState(false);
  const handleToggle = () => {
    // setOn(callBack) => setOn(preStart => !preStarte)
    setOn((on) => {
      return !on;
    });
  };

  return (
    <div>
